import { useEffect } from "react"
import { useState } from "react"
import AddTodo from "./AddTodo"
import { TodoList } from "./TodoList"

const getTodos =()=> {
    return fetch("http://localhost:8080/posts")
    .then(res=>res.json())
}

const addTodos = (todo)=> {
    return fetch("http://localhost:8080/posts",{
        method: "POST",
        headers:{
            "Content-Type": "application/json"
        },
        body: JSON.stringify(todo)
    })
    .then(res=>res.json())
}

function Todo(){
    const [todos, setTodos] = useState([])

    useEffect(()=>{
       
    },[])

    const handleAdd = (text)=> {
        const item = {
            title: text,
            status: false
        }
        addTodos(item)
        .then(res=>{
            // console.log(res)
        })
    }
    return (
        <div>
            <AddTodo handleAdd={handleAdd} />
            {
                todos.map(item=>
                    <TodoList 
                        key={item.id} 
                        titel={item.title} 
                        status={item.status} 
                        id={item.id}/>)
            }
        </div>
    )
}

export default Todo
