import { useState } from "react"

// const card = () => {
    const data=[
        {title: "Noodle" , price: 30, id:1},
        {title: "Biryani", price: 90, id: 2},
        {title:"Chips", price:10, id: 3},
    ]
// }

function Carts(){
const [amount, setAmount] = useState(0);
const[count, setCount] = useState(0)
// const handleCount = (count)=>{
//     setCount((prev)=> prev + count)
// }
const handleAmount=(amt)=> {
    setAmount((prev)=> prev + amt);
    // console.log(amount);
}
console.log(Carts);
    return (
        <div className="container">
            {data.map((e)=>(
                {count},
                <div className="listitem">
                    <div>{e.title}</div>
                    <div>{e.price}</div>
                    <div className="btn">
                        {/* <h1>Hello : {amount} </h1> */}
                        <button onClick={()=>handleAmount(e.price) (setCount(count+1))} >+</button>
                        <button disabled={count===0} >{count}</button>
                        <button disabled={count===0} onClick={()=>handleAmount(-e.price) (setCount(count-1))} >-</button>
                    </div>
               </div> 
            ))}
            <h1>Total: {amount}</h1>
        </div>
    )
}

export default Carts
