import { useEffect } from "react"
import { useState } from "react"
import AddTodo from "./AddTodo"
import { TodoList } from "./TodoList"

const getTodos =()=> {
    return fetch("http://localhost:8080/posts")
    .then(res=>res.json())
}

const addTodos = (todo)=> {
    return fetch("http://localhost:8080/posts",{
        method: "POST",
        headers:{
            "Content-Type": "application/json"
        },
        body: JSON.stringify(todo)
    })
    .then(res=>res.json())
}

function Todo(){
    const [loading, setLoading] = useState();
    const [todos, setTodos] = useState([])

    useEffect(()=>{
       handleGetTodos()
    },[])
    const handleGetTodos= () => {
        setLoading(true)
        return getTodos()
        .then(res=>{
            setLoading(false)
            // console.log(res)
            setTodos(res)
        })
        .catch(err=>{
            setLoading(false)
        })
    }

    const handleAdd = (text)=> {
        const item = {
            title: text,
            status: false
        }
        setLoading(true)
        addTodos(item)
        .then(res=>{
            // console.log(res)
            handleGetTodos()
        })
        .catch(err=>{
            setLoading(false)
        })

        
    }
    return (
        <div>
            <AddTodo handleAdd={handleAdd} />
            <div>
                {
                    
                }
            </div>
            {
                todos.map(item=>
                    <TodoList 
                        key={item.id} 
                        titel={item.title} 
                        status={item.status} 
                        id={item.id}/>)
            }
        </div>
    )
}

export default Todo
