import { useEffect } from "react"
import { useState } from "react"
import AddTodo from "./AddTodo"
import { TodoList } from "./TodoList"

const getTodos =()=> {
    return fetch("http://localhost:8080/posts")
    .then(res=>res.json())
}

const addTodos = (todo)=> {
    return fetch("http://localhost:8080/posts",{
        method: "POST",
        headers:{
            "Content-Type": "application/json"
        },
        body: JSON.stringify(todo)
    })
    .then(res=>res.json())
}

const toggleTodo = (id, newStatus)=> {
    return fetch(`http://localhost:8080/posts/${id}`,{
        method: "PATCH",
        headers:{
            "Content-Type": "application/json"
        },
        body: JSON.stringify({ status: newStatus })
    })
    .then(res=>res.json())
}

const deleteTodo = (id)=> {
    return fetch(`http://localhost:8080/posts/${id}`,{
        method: "DELETE",
        headers:{
            "Content-Type": "application/json"
        }
    })
    .then(res=>res.json())
}

function Todo(){
    const [loading, setLoading] = useState();
    const [todos, setTodos] = useState([])

    useEffect(()=>{
       handleGetTodos()
    },[])

    const handleGetTodos= () => {
        setLoading(true)
        return getTodos()
        .then(res=>{
            setLoading(false)
            // console.log(res)
            setTodos(res)
        })
        .catch(err=>{
            setLoading(false)
        })
    }

    const handleAdd = (text)=> {
        const item = {
            title: text,
            status: false
        }
        setLoading(true)
        addTodos(item)
        .then(res=>{
            // console.log(res)
            handleGetTodos()
        })
        .catch(err=>{
            setLoading(false)
        })

        
    }

    const handleToggle = (id, newStatus) => {
        setLoading(true)
        toggleTodo(id, newStatus)
        .then(res=>{
            handleGetTodos()
        })
        .catch(err=>{
            setLoading(false)
        })
    }

    const handleDelete = (id) => {
        setLoading(true)
        toggleTodo(id, newStatus)
        .then(res=>{
            handleGetTodos()
        })
        .catch(err=>{
            setLoading(false)
        })
    }

    return (
        <div>
            <div>
                {loading && "Loading!"}
            </div>
            <AddTodo handleAdd={handleAdd} />
            
            {
                todos.map(item=>
                    <TodoList 
                        key={item.id} 
                        titel={item.title} 
                        status={item.status} 
                        id={item.id}
                        handleToggle={handleToggle}
                        />)
            }
        </div>
    )
}

export default Todo
