import { useEffect } from "react"
import { useState } from "react"
import { addTodos, deleteTodo, getTodos, toggleTodo } from "../../api/todos";
import AddTodo from "./AddTodo"
import { TodoList } from "./TodoList"


function Todo(){
    const [loading, setLoading] = useState();
    const [todos, setTodos] = useState([])

    useEffect(()=>{
       handleGetTodos()
    },[])

    const handleGetTodos= () => {
        setLoading(true)
        return getTodos()
        .then(res=>{
            setLoading(false)
            // console.log(res)
            setTodos(res)
        })
        .catch(err=>{
            setLoading(false)
        })
    }

    const handleAdd = (text)=> {
        const item = {
            title: text,
            status: false
        }
        setLoading(true)
        addTodos(item)
        .then(res=>{
            // console.log(res)
            handleGetTodos()
        })
        .catch(err=>{
            setLoading(false)
        })

        
    }

    const handleToggle = (id, newStatus) => {
        setLoading(true)
        toggleTodo(id, newStatus)
        .then(res=>{
            handleGetTodos()
        })
        .catch(err=>{
            setLoading(false)
        })
    }

    const handleDelete = (id) => {
        setLoading(true)
        deleteTodo(id)
        .then(res=>{
            handleGetTodos()
        })
        .catch(err=>{
            setLoading(false)
        })
    }

    return (
        <div>
            <div>
                {loading && "Loading!"}
            </div>
            <AddTodo handleAdd={handleAdd} />
            <h3>PENDING</h3>
            {
                todos.map(item=>
                    <TodoList 
                        key={item.id} 
                        titel={item.title} 
                        status={item.status} 
                        id={item.id}
                        handleToggle={handleToggle}
                        handleDelete={handleDelete}
                        />)
            }
            <h3>Completed</h3>
        </div>
    )
}

export default Todo
